SELECT dbo_porel.jobnum, IIF(dbo_joboper.opcode IS NULL, "", dbo_joboper.opcode) AS opcode, \n       dbo_porel.jobseqtype, IIF(dbo_opmaster.shortchar01 IS NULL, "", dbo_opmaster.shortchar01) AS shortchar01, \n       dbo_porel.ponum, dbo_porel.poline, dbo_podetail.unitcost\nFROM ((dbo_porel \nLEFT JOIN dbo_joboper ON (dbo_porel.assemblyseq = dbo_joboper.assemblyseq OR dbo_joboper.assemblyseq IS NULL) \n    AND (dbo_porel.jobseq = dbo_joboper.oprseq OR dbo_joboper.oprseq IS NULL) \n    AND (dbo_porel.jobnum = dbo_joboper.jobnum OR dbo_joboper.jobnum IS NULL)) \nLEFT JOIN dbo_opmaster ON dbo_joboper.opcode = dbo_opmaster.opcode) \nLEFT JOIN dbo_podetail ON (dbo_pole.poline = dbo_podetail.poline OR dbo_podetail.poline IS NULL) \n    AND (dbo_porel.ponum = dbo_podetail.ponum OR dbo_podetail.ponum IS NULL)\nWHERE dbo_porel.jobnum="367000003"