-- Step 1: Backup\nCREATE TABLE backup_table AS SELECT * FROM your_table;\n\n-- Step 2: Disable foreign keys\nALTER TABLE other_table DISABLE CONSTRAINT fk_constraint_name;\n\n-- Step 3: Add new column\nALTER TABLE your_table ADD number_col_new NUMBER(22, 2);\n\n-- Step 4: Copy data with rounding if necessary\nUPDATE your_table\nSET number_col_new = ROUND(number_col_old, 2);\n\n-- Step 5: Validate Data\nSELECT COUNT(*) FROM your_table WHERE ROUND(number_col_old, 2) != number_col_new;\n\n-- Step 6: Drop old column and rename new one (if validation passes)\nALTER TABLE your_table DROP COLUMN number_col_old;\nALTER TABLE your_table RENAME COLUMN number_col_new TO number_col_old;\n\n-- Step 7: Re-enable constraints\nALTER TABLE other_table ENABLE CONSTRAINT fk_constraint_name;