-- Create a temporary table to hold the bank-account pairs\nCREATE TABLE #BankAccountPairs (\n    bank INT,\n    account VARCHAR(50)\n);\n\n-- Insert the list of bank-account pairs into the temp table\nINSERT INTO #BankAccountPairs (bank, account) VALUES \n(1, 'ACC001'),\n(2, 'ACC002'),\n-- Add more pairs as needed\n(3, 'ACC003');\n\n-- Perform the query using the temporary table\nSELECT b.date, SUM(b.amount) AS total_amount\nFROM balances b\nINNER JOIN #BankAccountPairs bp ON b.bank = bp.bank AND b.account = bp.account\nGROUP BY b.date;\n\n-- Drop the temporary table after use (optional)\nDROP TABLE #BankAccountPairs;