-- Enable Event Scheduler first\nSET GLOBAL event_scheduler = ON;\n\nDELIMITER //\n\nCREATE EVENT cleanup_amoreAgentTST01\nON SCHEDULE EVERY 1 HOUR -- Adjust as needed (could be daily, etc.)\nDO\nBEGIN\n    DECLARE table_size INTEGER;\n    DECLARE threshold INTEGER;\n\n    SET threshold = 500000;  -- Threshold in bytes\n\n    SELECT SUM(OCTET_LENGTH(data)) INTO table_size FROM amoreAgentTST01;\n\n    IF table_size > threshold THEN\n        DELETE FROM amoreAgentTST01 \n        ORDER BY updatetime ASC \n        LIMIT 1;\n    END IF;\nEND //\n\nDELIMITER ;