CREATE TABLE Party (\n    id INT PRIMARY KEY,\n    name VARCHAR(255),\n    address TEXT,\n    type ENUM('customer', 'employee', 'partner')\n);\n\nCREATE TABLE Customer (\n    party_id INT PRIMARY KEY,\n    customer_specific_info VARCHAR(255),\n    FOREIGN KEY (party_id) REFERENCES Party(id)\n);\n\nCREATE TABLE Employee (\n    party_id INT PRIMARY KEY,\n    employee_specific_info VARCHAR(255),\n    FOREIGN KEY (party_id) REFERENCES Party(id)\n);