-- Get the entire hierarchy starting from a specific node (e.g., RecordID = 1)\nWITH RECURSIVE Hierarchy AS (\n    SELECT RecordID, ParentID, LevelID, Name, DataField\n    FROM ReportingHierarchy\n    WHERE RecordID = 1 -- Starting point\n\n    UNION ALL\n\n    SELECT rh.RecordID, rh.ParentID, rh.LevelID, rh.Name, rh.DataField\n    FROM ReportingHierarchy rh\n    INNER JOIN Hierarchy h ON rh.ParentID = h.RecordID\n)\nSELECT * FROM Hierarchy;