-- Dimension Table for Status\nCREATE TABLE DimStatus (\n    StatusID INT PRIMARY KEY,\n    Status VARCHAR(10),\n    Description TEXT\n);\n\n-- Example Fact Table using the DimStatus\nCREATE TABLE ReservationFact (\n    ReservationID INT PRIMARY KEY,\n    RoomNumber INT,\n    StatusID INT,\n    CheckInDate DATE,\n    CheckOutDate DATE,\n    FOREIGN KEY (StatusID) REFERENCES DimStatus(StatusID)\n);