CREATE FUNCTION dbo.ConvertToBase32 (@decimalNumber BIGINT)\nRETURNS VARCHAR(255)\nAS\nBEGIN\n    DECLARE @base32Chars CHAR(32) = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ234567';\n    DECLARE @output VARCHAR(255) = '';\n    DECLARE @remainder INT;\n    \n    -- Ensure the input is not zero, as Base32 conversion of zero is "0"\n    IF @decimalNumber = 0\n        RETURN '0';\n\n    WHILE @decimalNumber > 0\n    BEGIN\n        SET @remainder = @decimalNumber % 32;\n        SET @output = SUBSTRING(@base32Chars, @remainder + 1, 1) + @output;\n        SET @decimalNumber = @decimalNumber / 32;\n    END\n\n    RETURN @output;\nEND