-- Configure SQL Server to use Mixed Mode authentication\nUSE master;\nGO\nEXEC sp_configure 'show advanced options', 1;\nRECONFIGURE;\nGO\nEXEC sp_configure 'mixed mode', 1;\nRECONFIGURE;\nGO\n\n-- Create a new login with restricted permissions for your web application\nCREATE LOGIN [WebAppUser] WITH PASSWORD = '<StrongPassword>', CHECK_POLICY = ON, CHECK_EXPIRATION = OFF;\nGO\n\nUSE YourDatabaseName;\nGO\nCREATE USER [WebAppUser] FOR LOGIN [WebAppUser];\nGO\nGRANT SELECT, INSERT, UPDATE ON dbo.YourTable TO [WebAppUser];\n-- Add other permissions as necessary