CREATE TABLE POSTS (\n       POST_ID INT PRIMARY KEY,\n       TITLE VARCHAR(255),\n       CONTENT TEXT,\n       CATEGORY_ID INT,\n       TOPIC_ID INT,\n       CHECK ((CATEGORY_ID IS NOT NULL AND TOPIC_ID IS NULL) OR\n              (CATEGORY_ID IS NULL AND TOPIC_ID IS NOT NULL)),\n       FOREIGN KEY (CATEGORY_ID) REFERENCES CATEGORY(ID)\n           ON DELETE SET NULL,\n       FOREIGN KEY (TOPIC_ID) REFERENCES TOPIC(ID)\n           ON DELETE SET NULL\n   );