WITH RECURSIVE NumberSeries AS (\n    -- Anchor member: start from 1 or minimal code - 1 if you want to consider codes below the lowest assigned\n    SELECT MIN(code) - 1 AS num FROM element_codes\n    \n    UNION ALL\n\n    -- Recursive member: increment by 1 to generate next number\n    SELECT num + 1 \n    FROM NumberSeries \n    WHERE num < (SELECT MAX(code) FROM element_codes)\n)\n\n-- Select numbers not present in your table\nSELECT num\nFROM NumberSeries\nLEFT JOIN element_codes ON element_codes.code = num\nWHERE element_codes.code IS NULL;