SELECT \n    serialnumber,\n    partnb,\n    MAX(CASE WHEN id = 'AGR' THEN REPLACE(actual, ',', '.') END) AS AGR,\n    MAX(CASE WHEN id = 'AGR' THEN nominal END) AS AGR_nominal,\n    MAX(CASE WHEN id = 'APR' THEN REPLACE(actual, ',', '.') END) AS APR,\n    MAX(CASE WHEN id = 'APR' THEN nominal END) AS APR_nominal,\n    MAX(CASE WHEN id = 'DCI' THEN REPLACE(actual, ',', '.') END) AS DCI,\n    MAX(CASE WHEN id = 'DCI' THEN nominal END) AS DCI_nominal\nFROM \n    your_table_name\nGROUP BY \n    serialnumber,\n    partnb;