DECLARE @a INT \nDECLARE @sql NVARCHAR(MAX)  -- Declare a variable to hold the dynamic SQL statement\n\nSET @a = 1\n\nWHILE @a < 30\nBEGIN\n    SET @sql = N'\n        UPDATE source_temp \n        SET pmt_90_day = pmt_90_day + convert(money, ''trans_total_'' + CAST(@b AS VARCHAR))\n        WHERE convert(datetime, ''effective_date_'' + CAST(@b AS VARCHAR)) <= dateadd(day, 90, ORSA_CHARGE_OFF_DATE)\n        AND DRC_FLAG_'' + CAST(@b AS VARCHAR) + ''' = ''C'''\n    \n    -- Execute the dynamic SQL statement\n    EXEC sp_executesql @sql, N'@b INT', @b = @a\n    \n    SET @a = @a + 1\nEND