CREATE TABLE POSTS (\n       POST_ID INT PRIMARY KEY,\n       TITLE VARCHAR(255),\n       CONTENT TEXT,\n       PARENT_ID INT NOT NULL,\n       PARENT_TYPE ENUM('CATEGORY', 'TOPIC') NOT NULL,\n       FOREIGN KEY (PARENT_ID) REFERENCES CATEGORY(ID)\n       ON DELETE CASCADE,\n       FOREIGN KEY (PARENT_ID) REFERENCES TOPIC(ID)\n       ON DELETE CASCADE\n   );