DECLARE\n    l_start_time TIMESTAMP;\n    l_end_time   TIMESTAMP;\n    l_elapsed      INTERVAL DAY TO SECOND(3);\n    l_data       RAW(2097152); -- Example buffer to hold data, adjust size as needed\n    l_hash       RAW(20); -- Hash result will be 20 bytes for SHA-1\n\nBEGIN\n    FOR i IN 4..30 LOOP\n        -- Simulate input data of size i MB\n        SELECT UTL_I18N.STRING_TO_RAW(LPAD('A', POWER(1024, 2) * i, 'A'), 'AL32UTF8') INTO l_data FROM dual;\n\n        -- Record start time\n        l_start_time := SYSTIMESTAMP;\n        \n        -- Compute SHA-1 hash\n        BEGIN\n            l_hash := DBMS_CRYPTO.HASH(l_data, DBMS_CRYPTO.HASH_SH1);\n        END;\n\n        -- Record end time and calculate duration\n        l_end_time := SYSTIMESTAMP;\n        l_elapsed := l_end_time - l_start_time;\n\n        -- Output the result\n        DBMS_OUTPUT.PUT_LINE('SHA-1 hash of ' || i || 'MB data took ' || EXTRACT(SECOND FROM l_elapsed) || '.' || \n                             TO_CHAR(EXTRACT(MILLISECOND FROM l_elapsed), 'FM000') || ' seconds');\n    END LOOP;\nEND;\n/