-- Create user for SELECT queries with only SELECT privileges\nCREATE USER 'readonly_user'@'localhost' IDENTIFIED BY 'secure_password1';\nGRANT SELECT ON your_database.* TO 'readonly_user'@'localhost';\n\n-- Create user for UPDATE/DELETE queries with additional privileges\nCREATE USER 'writeuser'@'localhost' IDENTIFIED BY 'secure_password2';\nGRANT SELECT, INSERT, UPDATE, DELETE ON your_database.* TO 'writeuser'@'localhost';