ALTER PROCEDURE dbo.spSelectBenchEvent\n(\n    @EVENT_DATE DATETIME // Use DATETIME as DATE datatype is not available in SQL Server 2005\n)\nAS\nBEGIN\n    SET NOCOUNT ON;\n\n    SELECT ID, EID, BENCHID, PACCODE, START_TIME, END_TIME\n    FROM tbl_benchbook\n    WHERE \n        CONVERT(DATE, START_TIME) = CONVERT(DATE, @EVENT_DATE) // Compare only the date part\n    ORDER BY START_TIME;\nEND