-- Declare a temporary table to store results\nCREATE TABLE #ApplicationSummary (\n    application_id INT,\n    NAME_LAST NVARCHAR(255),\n    NAME_FIRST NVARCHAR(255),\n    status NVARCHAR(255),\n    average_score DECIMAL(5, 2),\n    review_count INT\n);\n\n-- Insert the data into the temporary table using a single query\nINSERT INTO #ApplicationSummary (application_id, NAME_LAST, NAME_FIRST, status, average_score, review_count)\nSELECT \n    a.application_id,\n    a.NAME_LAST,\n    a.NAME_FIRST,\n    ca.status,\n    AVG(r.score) AS average_score,\n    COUNT(r.review_id) AS review_count\nFROM \n    application a\nJOIN \n    committee_applications ca ON a.application_id = ca.application_id\nLEFT JOIN \n    reviews r ON a.application_id = r.application_id\nGROUP BY \n    a.application_id, a.NAME_LAST, a.NAME_FIRST, ca.status;\n\n-- Select the data from the temporary table to return results\nSELECT * FROM #ApplicationSummary;\n\n-- Clean up by dropping the temporary table\nDROP TABLE #ApplicationSummary;