SELECT \n  height,\n  @percentile := (@row_num / @total_rows) * 100 AS percentile\nFROM your_table_name,\n(SELECT @row_num := 0, @total_rows := ( SELECT COUNT(*) FROM your_table_name )) AS t\nORDER BY height;\n\n-- And to get the middle third:\nSELECT height\nFROM (\n  SELECT \n    height,\n    @percentile := (@row_num / @total_rows) * 100 AS percentile\n  FROM your_table_name,\n  (SELECT @row_num := 0, @total_rows := ( SELECT COUNT(*) FROM your_table_name )) AS t\n  ORDER BY height\n) as ordered_data\nWHERE percentile BETWEEN 30 AND 70;