SELECT \n    p.ParentName, \n    c.CountPropertys, \n    c.DateTimeStamp\nFROM \n    dbo_Parents p\nINNER JOIN (\n    SELECT \n        ParentID,\n        MAX(DateTimeStamp) AS LatestDateTimeStamp\n    FROM \n        dbo_ChildEntry\n    GROUP BY \n        ParentID\n) latestChild ON p.ParentID = latestChild.ParentID\nINNER JOIN dbo_ChildEntry c ON latestChild.ParentID = c.ParentID AND latestChild.LatestDateTimeStamp = c.DateTimeStamp;