CREATE OR REPLACE TRIGGER trg_validate_postcode\nBEFORE INSERT OR UPDATE ON your_table_name\nFOR EACH ROW BEGIN\n  IF NOT REGEXP_LIKE(:NEW.postcode, '^([Gg][Ii][Rr] 0[Aa]{2})|((([A-Za-z][0-9]{1,2})|(([A-Za-z][A-Ha-hJ-Yj-y][0-9]{1,2})|(([A-Za-z][0-9][A-Za-z])|([A-Za-z][A-Ha-hJ-Yj-y][0-9]?[A-Za-z])))) [0-9][A-Za-z]{2})$') THEN\n    RAISE_APPLICATION_ERROR(-20102, 'Invalid UK postcode format');\n  END IF;\nEND trg_validate_postcode;\n/