-- Enable Full Text Search for the database\nEXEC sp_fulltext_database 'enable';\n\nUSE [YourDatabaseName];\n\n-- Create Catalog\nEXEC sp_fulltext_catalog 'MyCatalog', 'create';\n\n-- Add Table to Catalog\nEXEC sp_fulltext_table '[dbo].[YourTable]', 'create', 'MyCatalog', '[Primary Key Index Name]';\n\n-- Add Columns to the Full-Text Index\nEXEC sp_fulltext_column '[dbo].[YourTable]', '[YourVarcharColumn1]', 'add';\nEXEC sp_fulltext_column '[dbo].[YourTable]', '[YourVarcharColumn2]', 'add';\n\n-- Activate and Start Population of Full Text Catalog\nEXEC sp_fulltext_table '[dbo].[YourTable]', 'activate';\nEXEC sp_start_fulltext_catalog 'MyCatalog', 'full';