-- Switch to your database (replace 'MyDatabase' with your actual database name)\nUSE MyDatabase;\nGO\n\n-- Check current permissions for the public role on MyView\nEXEC sp_helprotect 'MyView', NULL, 'PUBLIC';\n\n-- Ensure no DENY exists on MyView for PUBLIC or any role/user that Test might be part of\nSELECT * FROM sys.database_permissions \nWHERE major_id = OBJECT_ID('dbo.MyView')\n  AND (grantee_principal_id = USER_ID('Public') OR grantee_principal_id IN (\n    SELECT role_principal_id \n    FROM sys.database_role_members AS rm \n    INNER JOIN sys.database_principals AS r ON rm.role_principal_id = r.principal_id\n    WHERE member_principal_id = USER_ID('Test')\n  ));\n\n-- If you find DENY, consider if it can be removed:\n-- REVOKE ALL ON dbo.MyView FROM [role_name];