DELIMITER //\n\nCREATE TRIGGER before_setting_default\nBEFORE INSERT ON your_table_name\nFOR EACH ROW\nBEGIN\n    IF NEW.isDefault = 1 THEN\n        UPDATE your_table_name\n        SET isDefault = 0\n        WHERE FormID = NEW.FormID AND isDefault = 1;\n    END IF;\nEND; //\n\nCREATE TRIGGER before_updating_default\nBEFORE UPDATE ON your_table_name\nFOR EACH ROW\nBEGIN\n    IF NEW.isDefault = 1 THEN\n        UPDATE your_table_name\n        SET isDefault = 0\n        WHERE FormID = NEW.FormID AND id <> NEW.id AND isDefault = 1;\n    END IF;\nEND; //\n\nDELIMITER ;