(SELECT 'news' as type, news.newsTitle as title, newsBody, postedOn, NULL as reviewSummary, NULL as ourScore, NULL as gameName, NULL as gameImage \n FROM news \n ORDER BY postedOn DESC LIMIT 5)\n\nUNION ALL\n\n(SELECT 'review' as type, review.gameID as title, review.reviewSummary as newsBody, review.postedOn, review.reviewSummary, review.ourScore, game.gameName, game.gameImage \n FROM review\n JOIN game ON game.gameID = review.gameID\n WHERE game.isPublished = 'y'\n ORDER BY postedOn DESC LIMIT 5)\n\nORDER BY postedOn DESC\nLIMIT 5;