DECLARE @value DECIMAL(18,6) = 12.550000\n\nSELECT \n    REPLACE(\n        RTRIM(CONVERT(VARCHAR, @value, 126)),\n        '.', -- remove trailing dot after zero decimal places\n        CASE \n            WHEN RIGHT(RTRIM(CONVERT(VARCHAR, @value, 126)), 1) = '.' THEN '' \n            ELSE '.'\n        END\n    ) AS FormattedValue\n\n-- Test with different values\nSELECT \n    REPLACE(\n        RTRIM(CONVERT(VARCHAR, value, 126)),\n        '.', -- remove trailing dot after zero decimal places\n        CASE \n            WHEN RIGHT(RTRIM(CONVERT(VARCHAR, value, 126)), 1) = '.' THEN '' \n            ELSE '.'\n        END\n    ) AS FormattedValue\nFROM (VALUES\n    (12.550000),\n    (14.456700),\n    (15.00)\n) AS test(value);