SELECT \n    p.Name AS 'Name',\n    p.Age AS 'Age',\n    (\n        SELECT \n            pc.Contact_Type AS 'ContactType',\n            pc.Contact_Value AS 'ContactValue'\n        FROM \n            Person_Contact_Details pc\n        WHERE \n            pc.id = p.id\n        FOR XML \n            PATH ('ContactDetail'), TYPE\n    ) AS 'ContactDetails'\nFROM \n    Person_Details p\nWHERE \n    p.id = 12\nFOR XML \n    PATH('PersonDetails'), ROOT('Root');