UPDATE work\nSET kilometers = calc_kilo,\n    kilo_test = test_kilo\nWHERE lc = NVL(rm.lc, lc) -- Use the current value of lc if rm.lc is NULL\n  AND ld = NVL(rm.ld, ld) -- Use the current value of ld if rm.ld is NULL\n  AND le = NVL(rm.le, le) -- Use the current value of le if rm.le is NULL\n  AND lf = NVL(rm.lf, lf) -- Use the current value of lf if rm.lf is NULL\n  AND code = rm.code\n  AND lcode = rm.lcode\n  AND user_id = username;