SELECT \n    t.extract('.').getStringVal() AS text\nFROM \n    TABLE(XMLSequence(\n        XMLTYPE('<xml>\n                    <node>\n                        <one>text</one>\n                    </node>\n                    <node>\n                        <two>text</two>\n                    </node>\n                    <node>\n                        <three>text</three>\n                    </node>\n                </xml>'\n            ).extract('//:*') \n    )) t\nWHERE \n    t.existsNode('./*') = 0;