BEGIN TRANSACTION;\n\n-- Assuming you need to create a new record and copy data over.\nDECLARE @NewContractID INT; -- This should be set by the new inserted row's ID\n\n-- Insert into Contracts table with new values.\nINSERT INTO Contracts (/*Columns*/)\nVALUES (/*Values*/, 'NewContractNumberValue');\n\nSET @NewContractID = SCOPE_IDENTITY();  -- Capture the newly created contract ID.\n\n-- Copy and update related records in other tables.\nINSERT INTO Orders (ContractID, /*Other Columns*/ ...)\nSELECT @NewContractID, /*Other Values*/ ...\nFROM Orders WHERE ContractID = @OldContractID;\n\n-- Repeat for all related tables...\n\n-- Optionally, delete old records if needed.\nDELETE FROM Orders WHERE ContractID = @OldContractID;\n-- Do the same for other tables as necessary.\n\nCOMMIT TRANSACTION;