BEGIN TRANSACTION;\n\n-- Update primary table (Contracts Table)\nDECLARE @OldContractID INT = 123;\nDECLARE @NewContractID INT = 456;\n\n-- Assuming there's a contractID in other tables as foreign key, update these first.\nUPDATE Orders SET ContractID = @NewContractID WHERE ContractID = @OldContractID;\nUPDATE Payments SET ContractID = @NewContractID WHERE ContractID = @OldContractID;\nUPDATE Invoices SET ContractID = @NewContractID WHERE ContractID = @OldContractID;\n-- Repeat for other tables as necessary\n\n-- Then update the Contracts table\nUPDATE Contracts \nSET ContractNumber = 'NewContractNumberValue'\nWHERE ContractID = @OldContractID;\n\nCOMMIT TRANSACTION;