DECLARE @Month INT = 1; -- January\nDECLARE @Year INT = 2009;\n\nSELECT \n    dbo.Post.UserId, \n    dbo.Post.Tags, \n    dbo.Post.CommentCount, \n    dbo.Post.Status,  \n    dbo.Post.PostDate, \n    dbo.Post.[Content], \n    dbo.Post.Title, \n    dbo.Post.PostId, \n    dbo.[User].DisplayName \nFROM  \n    dbo.Post \nINNER JOIN\n    dbo.[User] ON dbo.Post.UserId = dbo.[User].UserId \nWHERE \n    dbo.Post.PostDate >= DATEADD(MONTH, (@Year - 1900) * 12 + @Month - 1, 0)\n    AND dbo.Post.PostDate < DATEADD(MONTH, (@Year - 1900) * 12 + @Month, 0);