-- Set up DDL triggers to audit database schema changes\n   CREATE TRIGGER trg_AuditDDLChanges\n   ON DATABASE\n   FOR ALTER_TABLE, DROP_TABLE, CREATE_TABLE, ALTER_TRIGGER, DROP_TRIGGER, CREATE_TRIGGER\n   AS\n   BEGIN\n       INSERT INTO AuditLog (EventType, EventDate, CommandText)\n       VALUES ('DDL Change', getdate(), EVENTDATA().value('(/EVENT_INSTANCE/TSQLCommand/CommandText)[1]', 'nvarchar(max)'))\n   END