-- Table definition for 'Departments'\nCREATE TABLE Departments (\n    DepartmentID INT PRIMARY KEY,\n    DepartmentName VARCHAR(100) NOT NULL\n);\n\n-- Table definition for 'Employees' with non-null FK constraint\nCREATE TABLE Employees (\n    EmployeeID INT PRIMARY KEY,\n    EmployeeName VARCHAR(100) NOT NULL,\n    DepartmentID INT NOT NULL, -- Enforces that every employee is associated with a department\n    FOREIGN KEY (DepartmentID)\n        REFERENCES Departments(DepartmentID)\n);\n\n-- Table definition for 'Projects' with optional relationship to Employees\nCREATE TABLE Projects (\n    ProjectID INT PRIMARY KEY,\n    ProjectName VARCHAR(100) NOT NULL,\n    EmployeeID INT, -- Allows NULL if a project does not require an assigned employee\n    FOREIGN KEY (EmployeeID)\n        REFERENCES Employees(EmployeeID)\n);