SELECT \n    t1.*,\n    SUBSTRING_INDEX(SUBSTRING_INDEX(fldInputField, ',', numbers.n), ',', -1) AS matched_value\nFROM \n    aTable t1\nJOIN \n    (SELECT 1 n UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5) numbers\nON \n    CHAR_LENGTH(t1.fldInputField) - CHAR_LENGTH(REPLACE(t1.fldInputField, ',', '')) >= numbers.n - 1\nWHERE \n    FIND_IN_SET(SUBSTRING_INDEX(SUBSTRING_INDEX(t1.fldInputField, ',', numbers.n), ',', -1), t1.fldRoleList) > 0\nLIMIT 1;