-- Create table with separate columns for date and milliseconds\nCREATE TABLE events (\n    event_id NUMBER PRIMARY KEY,\n    event_date DATE NOT NULL,\n    event_time_ms NUMBER(3) NOT NULL CHECK (event_time_ms >= 0 AND event_time_ms < 1000)\n);\n\n-- Example of inserting data\nINSERT INTO events (event_id, event_date, event_time_ms) VALUES \n(1, TO_DATE('2023-10-05 14:48:00', 'YYYY-MM-DD HH24:MI:SS'), 987);\n\n-- Example query that combines date and milliseconds for sorting\nSELECT * FROM events ORDER BY event_date, event_time_ms;