CREATE TABLE sheets (\n    sheet_id INT AUTO_INCREMENT PRIMARY KEY,\n    name VARCHAR(255) NOT NULL,\n    locale VARCHAR(10) DEFAULT 'en'  -- for multilingual support\n);\n\nCREATE TABLE rows (\n    row_id INT AUTO_INCREMENT PRIMARY KEY,\n    sheet_id INT,\n    label VARCHAR(255),\n    FOREIGN KEY (sheet_id) REFERENCES sheets(sheet_id)\n);\n\nCREATE TABLE columns (\n    column_id INT AUTO_INCREMENT PRIMARY KEY,\n    sheet_id INT,\n    label VARCHAR(255),\n    FOREIGN KEY (sheet_id) REFERENCES sheets(sheet_id)\n);\n\nCREATE TABLE cells (\n    cell_id INT AUTO_INCREMENT PRIMARY KEY,\n    row_id INT,\n    column_id INT,\n    value TEXT,\n    FOREIGN KEY (row_id) REFERENCES rows(row_id),\n    FOREIGN KEY (column_id) REFERENCES columns(column_id)\n);