-- Assume we have a Table [Names] with columns: FirstName, LastName, PhoneticFirstName\n-- Also assume we have a table [NameVariations] with columns: Name, Variation\n\nDECLARE @SearchFirstName NVARCHAR(50) = 'Bill';\nDECLARE @SearchLastName NVARCHAR(50) = 'Smith';\n\n-- Using LIKE for direct matching (adjust collation if needed for case-insensitive)\nSELECT *\nFROM Names\nWHERE LastName = @SearchLastName\n  AND (FirstName = @SearchFirstName\n       OR PhoneticFirstName = \n         (\n           SELECT PhoneticCode\n           FROM NamePhonetics\n           WHERE Name = @SearchFirstName\n         )\n      );\n\n-- Using a subquery to find variations in the NameVariations table\nSELECT *\nFROM Names\nWHERE LastName = @SearchLastName\n  AND (FirstName IN \n       (\n         SELECT Variation\n         FROM NameVariations\n         WHERE Name = @SearchFirstName\n       ));