SELECT tblAssessment.fldValid, tblATraining.fldTID, tblATraining.fldTCrsID, \ntblCourses.fldCrsName, [fldCrsHrs]/8 AS Days, tblATraining.fldTLocAbr, \ntblDistrict.fldDistAbr, tblRegion.fldRegName, tblATraining.fldTDateStart, \ntblATraining.fldTDateEnd, tblATraining.fldTEnrolled, tblATraining.fldTPID, \ntblPersonnel.fldPName, tblAssessment.fldTrngSID, tblAssessment.Q1, \nIIf([fldValid]=True,IIf([Q1]>0,1,0),0) AS Q1Valid, tblAssessment.Q2, \nIIf([fldValid]=True,IIf([Q2]>0,1,0),0) AS Q2Valid, tblAssessment.Q3, \nIIf([fldValid]=True,IIf([Q3]>0,1,0),0) AS Q3Valid, tblAssessment.Q4, \nIIf([fldValid]=True,IIf([Q4]>0,1,0),0) AS Q4Valid, tblAssessment.Q5, \nIIf([fldValid]=True,IIf([Q5]>0,1,0),0) AS Q5Valid, tblAssessment.Q6, \nIIf([fldValid]=True,IIf([Q6]>0,1,0),0) AS Q6Valid, tblAssessment.Q7, \nIIf([fldValid]=True,IIf([Q7]>0,1,0),0) AS Q7Valid, tblAssessment.Q8, \ntblAssessment.Q9, \nIIf([fldValid]=True,IIf([Q9]>0,1,0),0) AS Q9Valid, tblAssessment.Q10, \ntblAssessment.Q25, tblAssessment.Q30\nFROM ((tblDistrict \n       LEFT JOIN tblRegion ON tblDistrict.fldDRegID = tblRegion.fldRegID) \n       RIGHT JOIN (((tblATraining \n                     LEFT JOIN tblCourses ON tblATraining.fldTCrsID = tblCourses.fldCrsID) \n                   LEFT JOIN tblPersonnel ON tblATraining.fldTPID = tblPersonnel.fldPID) \n                  LEFT JOIN tblLocations ON tblATraining.fldTLocAbr = tblLocations.fldLID) ON tblDistrict.fldDistAbr = tblATraining.fldTDistAbr) \n     LEFT JOIN tblAssessment ON tblATraining.fldTID = tblAssessment.fldTrngCID\nWHERE (((tblAssessment.fldValid)=True) \n        AND ((tblATraining.fldTCrsID) Like [forms]![fdlgRptCriteria].[selCrsCd]) \n        AND ((tblATraining.fldTDateStart) Between [forms]![fdlgRptCriteria].[seldate1] And [forms]![fdlgRptCriteria].[seldate2]) \n        AND ((tblAssessment.fldTrngSID) Is Not Null))\nUNION ALL\nSELECT tblNewAssessment.fldValid, tblNewATraining.fldTID, tblNewATraining.fldTCrsID, \ntblNewCourses.fldCrsName, [fldCrsHrs]/8 AS Days, tblNewATraining.fldTLocAbr, \ntblNewDistrict.fldDistAbr, tblNewRegion.fldRegName, tblNewATraining.fldTDateStart, \ntblNewATraining.fldTDateEnd, tblNewATraining.fldTEnrolled, tblNewATraining.fldTPID, \ntblNewPersonnel.fldPName, tblNewAssessment.fldTrngSID, tblNewAssessment.Q1, \nIIf([fldValid]=True,IIf([Q1]>0,1,0),0) AS Q1Valid, tblNewAssessment.Q2, \nIIf([fldValid]=True,IIf([Q2]>0,1,0),0) AS Q2Valid, tblNewAssessment.Q3, \nIIf([fldValid]=True,IIf([Q3]>0,1,0),0) AS Q3Valid, tblNewAssessment.Q4, \nIIf([fldValid]=True,IIf([Q4]>0,1,0),0) AS Q4Valid, tblNewAssessment.Q5, \nIIf([fldValid]=True,IIf([Q5]>0,1,0),0) AS Q5Valid, tblNewAssessment.Q6, \nIIf([fldValid]=True,IIf([Q6]>0,1,0),0) AS Q6Valid, tblNewAssessment.Q7, \nIIf([fldValid]=True,IIf([Q7]>0,1,0),0) AS Q7Valid, tblNewAssessment.Q8, \ntblNewAssessment.Q9, \nIIf([fldValid]=True,IIf([Q9]>0,1,0),0) AS Q9Valid, tblNewAssessment.Q10, \ntblNewAssessment.Q25, tblNewAssessment.Q30\nFROM ((tblNewDistrict \n       LEFT JOIN tblNewRegion ON tblNewDistrict.fldDRegID = tblNewRegion.fldRegID) \n       RIGHT JOIN (((tblNewATraining \n                     LEFT JOIN tblNewCourses ON tblNewATraining.fldTCrsID = tblNewCourses.fldCrsID) \n                   LEFT JOIN tblNewPersonnel ON tblNewATraining.fldTPID = tblNewPersonnel.fldPID) \n                  LEFT JOIN tblNewLocations ON tblNewATraining.fldTLocAbr = tblNewLocations.fldLID) ON tblNewDistrict.fldDistAbr = tblNewATraining.fldTDistAbr) \n     LEFT JOIN tblNewAssessment ON tblNewATraining.fldTID = tblNewAssessment.fldTrngCID\nWHERE (((tblNewAssessment.fldValid)=True) \n        AND ((tblNewATraining.fldTCrsID) Like [forms]![fdlgRptCriteria].[selCrsCd]) \n        AND ((tblNewATraining.fldTDateStart) Between [forms]![fdlgRptCriteria].[seldate1] And [forms]![fdlgRptCriteria].[seldate2]) \n        AND ((tblNewAssessment.fldTrngSID) Is Not Null));