WITH MinOrderCTE AS (\n    SELECT \n        CustomerID, \n        MIN(OrderDate) AS FirstOrderDate\n    FROM \n        Orders\n    GROUP BY \n        CustomerID\n)\nSELECT \n    c.CustomerName,\n    o.OrderDate\nFROM \n    Customers c\nJOIN \n    Orders o ON c.CustomerID = o.CustomerID\nJOIN \n    MinOrderCTE m ON c.CustomerID = m.CustomerID\nWHERE \n    o.OrderDate > m.FirstOrderDate;