-- Create a temp table with ID as an Identity column to auto-generate numbers\nCREATE TABLE #TempTable (\n    TempID INT IDENTITY(1,1) PRIMARY KEY,\n    Names VARCHAR(MAX),\n    Address VARCHAR(MAX)\n);\n\n-- Insert data from TableA into the temp table\nINSERT INTO #TempTable (Names, Address)\nSELECT Names, Address \nFROM TableA;\n\n-- Now insert data with sequentially generated IDs into TableB\nINSERT INTO TableB (ID, Names, Address)\nSELECT TempID, Names, Address \nFROM #TempTable;\n\n-- Cleanup: Drop the temporary table\nDROP TABLE #TempTable;