-- Begin transaction to ensure data integrity\n   START TRANSACTION;\n   \n   -- Get the current last internal ID for the company\n   SELECT LastInternalID INTO @last_id FROM CompanyIDTracker WHERE Company_id = X FOR UPDATE;\n   SET @new_id = @last_id + 1;\n   \n   -- Update the tracker table with the new last ID\n   UPDATE CompanyIDTracker SET LastInternalID = @new_id WHERE Company_id = X;\n   \n   -- Insert the new employee record with the incremented internal_id\n   INSERT INTO Employees (Company_id, internal_id, name, lastname) VALUES (X, @new_id, 'John', 'Doe');\n   \n   COMMIT;