CREATE TABLE Document (\n    ID INT PRIMARY KEY,\n    Title NVARCHAR(255)\n);\n\nCREATE TABLE Category (\n    ID INT PRIMARY KEY,\n    Title NVARCHAR(255)\n);\n\nCREATE TABLE Document_Category (\n    DocumentID INT,\n    CategoryID INT,\n    PRIMARY KEY (DocumentID, CategoryID),\n    FOREIGN KEY (DocumentID) REFERENCES Document(ID),\n    FOREIGN KEY (CategoryID) REFERENCES Category(ID)\n);