SELECT EXTRACT(MINUTE FROM NUMTODSINTERVAL(\n  TO_DATE('06/06/2009 16:00:00', 'DD/MM/YYYY HH24:MI:SS') \n- TO_DATE('06/06/2009 14:25:00', 'DD/MM/YYYY HH24:MI:SS'), 'DAY')) \n+ (EXTRACT(HOUR FROM NUMTODSINTERVAL(\n  TO_DATE('06/06/2009 16:00:00', 'DD/MM/YYYY HH24:MI:SS') \n- TO_DATE('06/06/2009 14:25:00', 'DD/MM/YYYY HH24:MI:SS'), 'DAY')) * 60) AS minutes_diff\nFROM dual;