SELECT \n    COALESCE(CASE WHEN Category LIKE '%truck%' OR Category2 LIKE '%truck%' THEN 'Truck'\n                   WHEN Category LIKE '%full size - pickup%' OR Category2 LIKE '%full size - pickup%' THEN 'Full Size - Pickup'\n                   WHEN Category LIKE '%sedan%' OR Category2 LIKE '%sedan%' THEN 'Sedan'\n                   WHEN Category LIKE '%convertible%' OR Category2 LIKE '%convertible%' THEN 'Convertible'\n              END, 'Other') AS StandardizedCategory,\n    COUNT(*) AS Count\nFROM Items\nGROUP BY \n    COALESCE(CASE WHEN Category LIKE '%truck%' OR Category2 LIKE '%truck%' THEN 'Truck'\n                   WHEN Category LIKE '%full size - pickup%' OR Category2 LIKE '%full size - pickup%' THEN 'Full Size - Pickup'\n                   WHEN Category LIKE '%sedan%' OR Category2 LIKE '%sedan%' THEN 'Sedan'\n                   WHEN Category LIKE '%convertible%' OR Category2 LIKE '%convertible%' THEN 'Convertible'\n              END, 'Other');