EXEC sp_dropserver 'YourLinkedServerName', 'droplogins'; -- Drop if it exists\n\nUSE [master];\nGO\n\nEXEC master.dbo.sp_addlinkedserver \n    @server = N'YourLinkedServerName', \n    @srvproduct=N'Microsoft SQL Server', \n    @provider=N'SQLNCLI10',  -- Ensure using a provider that supports SQL Server 2008\n    @datasrc=N'YourServerInstanceName';\n\nEXEC master.dbo.sp_addlinkedsrvlogin \n    @rmtsrvname = N'YourLinkedServerName',\n    @useself = N'True';\n    \nGO\n\n-- Security note: the following statement provides access to SQL Server\n-- by using Windows Authentication. Modify this if needed.\nEXEC master.dbo.sp_grantlogin @loginame=N'DOMAIN\Username'; -- Adjust this line based on your security setup