CREATE TRIGGER trg_DimensionTable_Delete\nON DimensionTable\nAFTER DELETE\nAS\nBEGIN\n    SET NOCOUNT ON;\n\n    -- Delete from FactTable where source_id matches deleted id(s)\n    DELETE FROM FactTable\n    WHERE source_id IN (SELECT id FROM DELETED);\n\n    -- Delete from FactTable where target_id matches deleted id(s)\n    DELETE FROM FactTable\n    WHERE target_id IN (SELECT id FROM DELETED);\nEND;