WITH RankedCars AS (\n    SELECT \n        make,\n        model,\n        year,\n        other_columns,  -- Replace other_columns with actual column names\n        ROW_NUMBER() OVER (PARTITION BY make, model ORDER BY year DESC) as rn\n    FROM cars\n)\nSELECT \n    make,\n    model,\n    year,\n    other_columns  -- Again, replace other_columns with actual column names\nFROM RankedCars\nWHERE rn = 1;