-- Replace 'YourDatabaseName', 'YourSchemaName', 'YourTableName', and 'YourColumnName' with your actual values.\nSELECT \n    o.name AS ProcedureName,\n    m.definition AS ProcedureDefinition\nFROM \n    sys.sql_modules AS m\nINNER JOIN \n    sys.objects AS o ON m.object_id = o.object_id\nWHERE \n    type IN ('P', 'PC')  -- P = Procedure, PC = Assembly (CLR) stored-proc\n    AND o.schema_id = SCHEMA_ID('YourSchemaName')\n    AND OBJECT_DEFINITION(m.object_id) LIKE '%YourTableName.YourColumnName%'\nORDER BY \n    o.name;