DECLARE @sql TABLE (script VARCHAR(MAX));\n\nINSERT INTO @sql(script)\nSELECT 'ALTER TABLE [' + \n       OBJECT_SCHEMA_NAME(f.parent_object_id) +\n       '].[' + OBJECT_NAME(f.parent_object_id) +\n       '] DROP CONSTRAINT [' + \n       f.name +']'\nFROM sys.foreign_keys AS f\nINNER JOIN sys.foreign_key_columns AS fc ON f.object_id = fc.constraint_object_id\nWHERE f.parent_object_id = OBJECT_ID('Machines') -- replace with your table name\nAND fc.parent_column_id = COLUMNPROPERTY(object_id('Machines'), 'IdArt', 'ColumnId') -- replace with your column name\n\nSELECT script FROM @sql;\n-- Uncomment the below line if you want to execute the generated scripts\n-- EXEC sp_executesql (N'SELECT * FROM OPENJSON((SELECT '[' + STRING_AGG(script,', ') WITHIN GROUP (ORDER BY 1) AS arr FROM @sql FOR JSON PATH, WITHOUT_ARRAY_WRAPPER)) AS a')